import React from 'react';
import { Grid, Paper, Typography } from '@mui/material';

const tips = [
  { title: 'Use LED Bulbs', description: 'Switching to LED bulbs can save up to 80% of energy compared to traditional bulbs.' },
  { title: 'Unplug Devices', description: 'Unplugging idle devices reduces energy waste and lowers bills.' },
  { title: 'Seal Windows', description: 'Proper sealing helps prevent heat loss and reduces heating costs.' },
  { title: 'Use Smart Thermostat', description: 'A smart thermostat optimizes heating and cooling to save energy.' },
];

const Dashboard = () => (
  <div style={{ padding: '20px' }}>
    <Typography variant="h4" gutterBottom>
      Energy Savings Tips
    </Typography>
    <Grid container spacing={3}>
      {tips.map((tip, index) => (
        <Grid item xs={12} sm={6} md={3} key={index}>
          <Paper style={{ padding: '20px', display: 'flex', justifyContent: 'space-between' }}>
            <div>
              <Typography variant="h6">{tip.title}</Typography>
              <Typography variant="body2">{tip.description}</Typography>
            </div>
            <div style={{ backgroundColor: '#e0e0e0', width: '50px', height: '50px' }} />
          </Paper>
        </Grid>
      ))}
    </Grid>
  </div>
);

export default Dashboard;
