import React from 'react';
import { Box, Grid, Card, CardContent, Typography } from '@mui/material';
import Navbar from '../../components/Navbar';
import Sidebar from '../../components/Sidebar';
import CircleIcon from '@mui/icons-material/Circle';
import SquareIcon from '@mui/icons-material/Square';
import ChangeHistoryIcon from '@mui/icons-material/ChangeHistory'; // Triangle

const Dashboard = () => {
  return (
    <Box sx={{ display: 'flex', height: '100vh' }}>
      {/* Sidebar with White Background */}
      <Sidebar sx={{ backgroundColor: 'white', width: '240px' }} />

      {/* Main content area */}
      <Box sx={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        {/* Navbar */}
        <Navbar />

        {/* Dashboard Content */}
        <Box sx={{ padding: 4 }}>
          <Typography variant="h4" gutterBottom>
            Energy Savings Tips
          </Typography>

          <Grid container spacing={3}>
            {["Use LED Bulbs", "Unplug Devices", "Seal Windows", "Use Smart Thermostat"].map((tip, index) => (
              <Grid item xs={12} sm={6} md={3} key={index}>
                <Card
                  sx={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    padding: 2,
                    borderRadius: '12px',
                    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
                  }}
                >
                  {/* Card Content */}
                  <CardContent sx={{ flex: 1 }}>
                    <Typography variant="h6" gutterBottom>
                      {tip}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Switching to LED bulbs can save up to 80% of energy compared to traditional bulbs.
                    </Typography>
                  </CardContent>

                  {/* Icon Section */}
                  <Box
                    sx={{
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'center',
                      justifyContent: 'center',
                      backgroundColor: '#f0f0f0',
                      padding: 1,
                      borderRadius: '8px',
                      width: '60px',
                    }}
                  >
                    <ChangeHistoryIcon sx={{ color: 'gray', marginBottom: 1 }} />
                    <SquareIcon sx={{ color: 'gray', marginBottom: 1 }} />
                    <CircleIcon sx={{ color: 'gray' }} />
                  </Box>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Box>
    </Box>
  );
};

export default Dashboard;
