import React from 'react';
import { Drawer, List, ListItem, ListItemIcon, ListItemText, Typography, Box } from '@mui/material';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import DashboardIcon from '@mui/icons-material/Dashboard';
import TipsAndUpdatesIcon from '@mui/icons-material/TipsAndUpdates';
import SpaIcon from '@mui/icons-material/Spa'; 
import ForumIcon from '@mui/icons-material/Forum';
import SettingsIcon from '@mui/icons-material/Settings';
import PersonIcon from '@mui/icons-material/Person';

const Sidebar = () => {
  return (
    <Drawer
      variant="permanent"
      sx={{
        width: 240,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: 240,
          boxSizing: 'border-box',
          backgroundColor: '#4a4a4a',
          color: 'white',
        },
      }}
    >
      <Box sx={{ padding: 2, textAlign: 'center' }}>
        <Typography variant="h5">EcosysTech</Typography>
      </Box>

      <List>
        {[
          { text: 'Energy Dashboard', icon: <DashboardIcon />, link: '/dashboard' },
          { text: 'Energy Usage', icon: <TipsAndUpdatesIcon />, link: '/energy-usage' },
          { text: 'Eco-Friendly', icon: <SpaIcon />, link: '/eco-friendly' },
          { text: 'Energy Saving Tips', icon: <TipsAndUpdatesIcon />, link: '/energy-tips' },
          { text: 'Community', icon: <ForumIcon />, link: '/community' },
        ].map((item, index) => (
          <ListItem
            button
            key={index}
            component={Link}
            to={item.link}
            sx={{
              textDecoration: 'none',
              color: 'black', // Text color for links
              backgroundColor: 'white', // Background color
              '&:hover': {
                backgroundColor: '#f5f5f5', // Lighter hover effect
              },
            }}
          >
            <ListItemIcon sx={{ color: 'black' }}>{item.icon}</ListItemIcon>
            <ListItemText primary={item.text} />
          </ListItem>
        ))}
      </List>

      <Box sx={{ position: 'absolute', bottom: 0, width: '100%' }}>
        <ListItem
          button
          component={Link}
          to="/profile"
          sx={{
            textDecoration: 'none',
            color: 'black',
            backgroundColor: 'white',
            '&:hover': {
              backgroundColor: '#f5f5f5',
            },
          }}
        >
          <ListItemIcon sx={{ color: 'black' }}>
            <PersonIcon />
          </ListItemIcon>
          <ListItemText primary="User" secondary="View profile" />
        </ListItem>

        <ListItem
          button
          component={Link}
          to="/settings"
          sx={{
            textDecoration: 'none',
            color: 'black',
            backgroundColor: 'white',
            '&:hover': {
              backgroundColor: '#f5f5f5',
            },
          }}
        >
          <ListItemIcon sx={{ color: 'black' }}>
            <SettingsIcon />
          </ListItemIcon>
          <ListItemText primary="Settings" />
        </ListItem>
      </Box>
    </Drawer>
  );
};

export default Sidebar;
